mapper.select:
SELECT DISTINCT t.id, t.application_id AS applicationId, t.prefix, t.business_license_number AS businessLicenseNumber, t.enterprise_code AS enterpriseCode, t.enterprise_name AS enterpriseName, t.industry_id AS industryId, t.area_id AS areaId, t.enterprise_type AS enterpriseType, t.telephone, t.email, t.legal_person AS legalPerson, t.main_person AS mainPerson, t.main_person_mobile AS mainPersonMobile, t.map_x AS mapX, t.map_y AS mapY, t.map_z AS mapZ, t.address AS address, t.is_sync AS isSync, t.`status`, t.gmt_create AS gmtCreate, t.gmt_update AS gmtUpdate, t.create_user AS createUser, t.update_user AS updateUser, t4.`name` AS areaName, t5.`name` AS industryName FROM ktf_enterprise t LEFT JOIN ktf_sys_user t2 ON t.id = t2.enterprise_id LEFT JOIN ktf_sys_user_enterprise t3 ON t2.id = t3.user_id LEFT JOIN ktf_sys_region t4 ON t.area_id = t4.id LEFT JOIN ktf_sys_industry t5 ON t.industry_id = t5.id
t.id=#{params.enterpriseId}
and t.enterprise_code=#{params.enterpriseCode}
and t.enterprise_type=#{params.enterpriseType}
and t.enterprise_name like '%${params.enterpriseName}%'
and t.area_id=#{params.areaId}
and t.industry_id=#{params.industrId}
and t2.id=#{params.userId}

